	.dbg	line,	"..\..\..\include\hucc\hucc-old-scroll.h", 23; ; forever limited by its choice to handle gaps between areas, and sorting.
	.dbg	line,	"..\..\..\include\hucc\hucc-old-scroll.h", 24; ;
